INSERT INTO `pages` (`id`, `menu_id`, `name`, `slug`, `info`, `content`, `created_at`, `updated_at`) VALUES
(2, 1, 'Lớp N5 với giáo viên', 'lop-n5-voi-giao-vien', 'Khoá học online với giáo viên, cung cấp kiến thức bổ sung về N5', '<div id=\"lession-cotent\" class=\"clearfix\">\r\n                    <h1 id=\"arc-title\"><span class=\"dark-red\">Giới thiệu khối lớp luyện thi N5 </span><span class=\"small-hide\"></span></h1>\r\n\r\n                    <div id=\"infoMsg\" style=\" padding-right:28px; width:130px\">\r\n                        <span></span>\r\n                        <a href=\"javascript:;\" class=\"x-icon closeBox\" rel=\"infoMsg\" id=\"newMsgClose\"></a>\r\n                    </div>\r\n\r\n                    <div id=\"newsInner\"><p></p>\r\n                        <p></p>\r\n                        <p></p>\r\n                        <p></p>\r\n                        <p></p>\r\n                        <p style=\"text-align: center;\"><span style=\"font-size: x-large;\"><strong><span style=\"color: #3366ff;\">GIỚI THIỆU LỚP <span style=\"color: #ff0000;\">N5 ONLINE HỌC VỚI GIÁO VIÊN <br></span></span></strong></span>\r\n                        </p>\r\n                        <p>&nbsp;</p>\r\n                        <p>&nbsp;</p>\r\n                        <p><span style=\"font-size: medium;\">Chương trình học Online được xây dựng theo lớp học thực tế, lớp học sử dụng các bài giảng theo chương trình lớp luyện thi đảm bảo đậu N5 của trường ngoại ngữ Việt Nhật (chương trình này được sử dụng cho lớp Du học sinh trường). </span>\r\n                        </p>\r\n                        <p><span style=\"font-size: medium;\">Lớp học sử dụng các phần mềm hỗ trợ, cùng các chức năng trên website tạo sự tương tác tự nhiên với giáo viên, tương tác giữa các học viên, tạo không khí lớp học 1 cách tự nhiên và thao tác đơn giản nhất. Ngoài giờ học trực tuyến với giáo viên, học viên còn được cung cấp các bài học offline, các bài nghe, bài trắc nghiệm bổ sung kiến thức.</span>\r\n                        </p>\r\n                        <p><span style=\"font-size: medium;\">Một số đặc điểm tính năng cơ bản trong lớp học Online:</span>\r\n                        </p>\r\n                        <p><strong><span style=\"font-size: medium;\">Tương tác giữa giáo&nbsp; viên và học viên:</span></strong>\r\n                        </p>\r\n                        <p style=\"padding-left: 30px;\"><span style=\"font-size: medium;\">+ Học viên nghe và đối thoại trực tiếp với giáo viên và các học viên khác trong lớp học.</span>\r\n                        </p>\r\n                        <p style=\"padding-left: 30px;\"><span style=\"font-size: medium;\">+ Giáo viên trực tiếp giảng bài, học viên nghe và nhìn trực tiếp bài giảng.</span>\r\n                        </p>\r\n                        <p style=\"padding-left: 30px;\"><span style=\"font-size: medium;\">+ Bài tập trắc nghiệm, các bài kiểm tra kiến thức sử dụng trong lớp học được thực hiện nhanh chóng, giáo viên quan sát và nhận xét ngay trong lớp.</span>\r\n                        </p>\r\n                        <p style=\"padding-left: 30px;\"><span style=\"font-size: medium;\">+ Bài tập online trên website, các bài luyện nghe được giáo viên cung cấp cho từng lớp theo ngày học.</span>\r\n                        </p>\r\n                        <p style=\"padding-left: 30px;\"><span style=\"font-size: medium;\">+ Mỗi buổi học bao gồm giáo viên dạy chính, 1 trợ giảng, 1 kỹ thuật viên. Kỹ thuật viên hỗ trợ học viên trong quá trình học đảm bảo lớp diễn ra liên tục.</span>\r\n                        </p>\r\n                        <p><strong><span style=\"font-size: medium;\">Chuẩn bị và thao tác trong lớp học:</span></strong>\r\n                        </p>\r\n                        <p style=\"padding-left: 30px;\"><span style=\"font-size: medium;\">+ 1 máy tính để bàn hoặc laptop kết nối mạng inter<span style=\"color: #ffffff;\">.</span>net.<br></span></p>\r\n                        <p style=\"padding-left: 30px;\"><span style=\"font-size: medium;\">+ Có Microphone và headphone tốt.</span>\r\n                        </p>\r\n                        <p style=\"padding-left: 30px;\"><span style=\"font-size: medium;\">+ Học viên phải học nơi yên tĩnh (nơi đặt máy tính). Trường hợp nơi học viên ồn, học viên phải tự tắt Micro để tránh ảnh hưởng đến lớp học.<br></span>\r\n                        </p>\r\n                        <p style=\"padding-left: 30px;\"><span style=\"font-size: medium;\">+ Học viên Online trước giờ học 5 phút để kiểm tra máy tính và đường truyền kết nối trước khi vào giờ học.</span>\r\n                        </p>\r\n                        <p style=\"padding-left: 30px;\"><span style=\"font-size: medium;\">+ Trước ngày khai giảng sẽ có hướng dẫn đăng nhập, thực tập thao tác, kiểm tra phần mềm,...<br></span>\r\n                        </p>\r\n                        <p>&nbsp;</p>\r\n                        <p></p>\r\n                        <div class=\"divider divider6\"></div>\r\n                        <p></p>\r\n                        <p>&nbsp;</p>\r\n                        <p style=\"text-align: center;\"><strong><span style=\"font-size: large; color: #ff0000;\">THÔNG TIN LỚP LUYỆN THI N5 ONLINE (Học với giáo viên)<br></span></strong>\r\n                        </p>\r\n                        <p><span style=\"font-size: medium; font-family: arial,helvetica,sans-serif;\"><span style=\"color: #0000ff;\"><strong>♦ </strong></span></span><span style=\"font-size: medium; font-family: arial,helvetica,sans-serif;\"><span style=\"color: #0000ff;\"><strong>Đối tượng:</strong></span></span></p>\r\n                        <p style=\"margin-left: 40px;\"><span style=\"font-size: medium; font-family: arial,helvetica,sans-serif;\">+ Học viên chưa từng học tiếng Nhật.<br></span>\r\n                        </p>\r\n                        <p style=\"margin-left: 40px;\"><span style=\"font-size: medium; font-family: arial,helvetica,sans-serif;\">+ Học viên đã học tiếng Nhật nhưng chưa có bằng N5 và có nhiều lỗ hổng kiến thức.</span>\r\n                        </p>\r\n                        <p style=\"margin-left: 40px;\"><span style=\"font-size: medium; font-family: arial,helvetica,sans-serif;\">+ Học viên không giới hạn về địa điểm.</span>\r\n                        </p>\r\n                        <p><span style=\"font-size: medium; font-family: arial,helvetica,sans-serif;\"><span style=\"color: #0000ff;\"><strong>♦ </strong></span></span><span style=\"font-size: medium; font-family: arial,helvetica,sans-serif;\"><span style=\"color: #0000ff;\"><strong>Thời lượng học: </strong><span style=\"color: #000000;\"> </span></span></span></p>\r\n                        <p style=\"margin-left: 40px;\"><span style=\"font-size: medium; font-family: arial,helvetica,sans-serif;\">+ Tổng thời lượng khóa <strong>6 tháng</strong>.<br></span>\r\n                        </p>\r\n                        <p style=\"margin-left: 40px;\"><span style=\"font-size: medium; font-family: arial,helvetica,sans-serif;\">+ <strong>3 buổi/ tuần</strong>.</span>\r\n                        </p>\r\n                        <p style=\"margin-left: 40px;\"><span style=\"font-size: medium; font-family: arial,helvetica,sans-serif;\">+ <strong>2 tiết/ buổi</strong>.<br></span>\r\n                        </p>\r\n                        <p style=\"margin-left: 40px;\"><span style=\"font-size: medium; font-family: arial,helvetica,sans-serif;\">+ <strong>45 phút/tiết học</strong>.</span>\r\n                        </p>\r\n                        <p style=\"margin-left: 40px;\"><span style=\"font-size: medium; font-family: arial,helvetica,sans-serif;\">(Tháng học tính theo đơn vị tuần: 4 tuần)<br></span>\r\n                        </p>\r\n                        <p><span style=\"font-size: medium; font-family: arial,helvetica,sans-serif;\"><span style=\"color: #0000ff;\"><strong>♦ </strong></span></span><span style=\"font-size: medium; font-family: arial,helvetica,sans-serif;\"><span style=\"color: #0000ff;\"><strong>Học phí:</strong></span><span style=\"color: #ff0000;\"><strong> 2.000.000</strong></span> VNĐ/ 1 tháng. Đóng học phí từng tháng.&nbsp; (tham khảo cách đóng học phí Online xem bên dưới<strong> </strong>)</span>\r\n                        </p>\r\n                        <p><span style=\"font-size: medium; font-family: arial,helvetica,sans-serif;\"><span style=\"color: #0000ff;\"><strong>♦ </strong></span></span><span style=\"font-size: medium; font-family: arial,helvetica,sans-serif;\"><span style=\"color: #0000ff;\"><strong>Số lượng:</strong> </span>5-10 học viên/ lớp.</span>\r\n                        </p>\r\n                        <p><span style=\"font-size: medium; font-family: arial,helvetica,sans-serif;\"><span style=\"color: #0000ff;\"><strong>♦ </strong></span><span style=\"color: #0000ff;\"><strong>Giáo viên:</strong></span></span>\r\n                        </p>\r\n                        <p style=\"margin-left: 40px;\"><span style=\"font-size: medium; font-family: arial,helvetica,sans-serif;\">+ 1 lớp 2 giáo viên chủ nhiệm.</span>\r\n                        </p>\r\n                        <p><span style=\"font-size: medium; font-family: arial,helvetica,sans-serif;\"><span style=\"color: #0000ff;\"><strong>♦ </strong></span></span><span style=\"font-size: medium; font-family: arial,helvetica,sans-serif;\"><span style=\"color: #0000ff;\"><strong>Giáo trình và tài liệu:</strong></span></span></p>\r\n                        <p style=\"margin-left: 40px;\"><span style=\"font-size: medium; font-family: arial,helvetica,sans-serif;\">+ Giáo trình được biên soạn riêng.</span>\r\n                        </p>\r\n                        <p style=\"margin-left: 40px;\"><span style=\"font-size: medium; font-family: arial,helvetica,sans-serif;\">+ Tổng hợp các giáo trình bổ sung các kỹ năng còn yếu cho học viên.<br></span>\r\n                        </p>\r\n                        <p style=\"margin-left: 40px;\"><span style=\"font-size: medium; font-family: arial,helvetica,sans-serif;\">+ Tài liệu sẽ được cu